const payload = msg.payload;

if (!payload.error) {
    const pollutedTempString = payload.result;
    const spaceSignIndex = pollutedTempString.indexOf(' ');
    if (spaceSignIndex > -1) {
        const tempString = pollutedTempString.substring(0, spaceSignIndex);
        const temp = parseFloat(tempString);
        return { 
            payload: { 
                result: temp, 
                error: false
            }
        };
    }
}

return { error: true }

const payload = msg.payload;

if (!payload.error) {
    const temp = parseFloat(payload.result);

    return { payload: temp };
}

return { error: true }

var isOn = (msg.payload == 0) ? false : true;
return {
    payload: { On : isOn }
};

var maxBrightness = 255;
var brightness = msg.payload;
var isOn = (brightness == 0) ? false : true;
var brightnessPercent = Math.round(brightness / maxBrightness * 100);
return {
    payload: { 
        "On" : isOn,
        "Brightness": brightnessPercent,
    }
};

//ampio dimmer accepts single input 0 - 255;

var maxBrightness = 255;
var output = -1;
if (msg.payload.On === true ) {
    output = maxBrightness;
} else if (msg.payload.On === false){
    output = 0;
} else if (msg.payload.Brightness != null) {
    output = Math.ceil(msg.payload.Brightness / 100 * 255);
}
if(output != -1 ) {
    return {
        payload: output
    };
}

if(
    msg == null || 
    msg.payload == null || 
    msg.payload.sunInSky == null
)
return 0;

var isNight = !msg.payload.sunInSky;
var currentHour = new Date().getHours();
var isBedTime = currentHour < 7 ? true : false;
var lightStatus = (isNight && !isBedTime) ? 1 : 0;

return {
    payload: lightStatus
}

if(msg.payload.sun == null) return null;
if(msg.payload.blindstate == null) return null;

var isSunInSky = msg.payload.sun.sunInSky;
var status = msg.payload.blindstate;

//return {payload: {'status':status,
//    sun: isSunInSky
//}};

if(status == "100" && isSunInSky == true) return {payload: 1};
if(status == "0" && isSunInSky == false) return {payload: 2};

return null;

if(msg.payload === null) return { payload: { status: -1, error: "Msg payload null" }};
var payload = msg.payload;
var weather = payload.weather;
var blindstate = payload.blindstate;
var blind = payload.blind;

if(weather === null) return { payload: { status: -1, error: "Weather null" }};
if(blindstate === null) return { payload: { status: -1, error: "Blindstate null" }};
if(blind === null) return { payload: { status: -1, error: "Blind null" }};

var newBlindPosition = blind.blindPosition;
var currentBlindPosition = Number.parseInt(msg.payload.blindstate);

if(weather.windspeed > 7) newBlindPosition == 100;
if(weather.rain > 0) newBlindPosition = 100;

if(newBlindPosition === currentBlindPosition) return { payload: {status: 0, info: "Blind postion doesn't change"}};

if(newBlindPosition == 100) return {payload: {status: 1, info: "Up"}};
if(newBlindPosition === 0) return {payload: {status: 2, info: "Down"}};

return null;

if(
    msg.payload.blindstate == null ||
    msg.payload.blind == null
) return null;

var newBlindPosition = msg.payload.blind.blindPosition;
var currentBlindPosition = Number.parseInt(msg.payload.blindstate);

if(newBlindPosition === currentBlindPosition) return null

if(newBlindPosition == 100) return {payload: 1};
if(newBlindPosition == 0) return {payload: 2};

return null;

if(msg.payload === null) return null;

return { 
    topic: 'weather', 
    payload: {
        clouds: msg.payload.clouds / 100,
        windspeed: msg.payload.windspeed,
        weather: msg.payload.weather,
        rain: msg.payload.rain,
        temp: msg.payload.tempc,
    }
}
